package jwt

import (
	"crypto/rand"
	"crypto/rsa"
	"testing"
)

func Test_SigningPS256(t *testing.T) {
	h := SigningPS256

	alg := h.Alg()
	signLength := h.SignLength()

	if alg != "PS256" {
		t.Errorf("Alg got %s, want %s", alg, "PS256")
	}
	if signLength != 512 {
		t.Errorf("SignLength got %d, want %d", signLength, 512)
	}

	var msg = "test-data"

	privateKey, err := rsa.GenerateKey(rand.Reader, 1024)
	if err != nil {
		t.Fatal(err)
	}

	publicKey := &privateKey.PublicKey

	signed, err := h.Sign([]byte(msg), privateKey)
	if err != nil {
		t.Fatal(err)
	}

	veri, err := h.Verify([]byte(msg), signed, publicKey)
	if err != nil {
		t.Fatal(err)
	}

	if !veri {
		t.Error("Verify fail")
	}

}

