func Test_SigningPS256_check(t *testing.T) {
	h := SigningPS256

	var prikey = ""
	var pubkey = ""

	var prikeyBytes = fromBase64(prikey)
	var pubkeyBytes = fromBase64(pubkey)

	privateKey, err := ParseRSAPrivateKeyFromDer(prikeyBytes)
	if err != nil {
		t.Fatal(err)
	}

	publicKey, err := ParseRSAPublicKeyFromDer(pubkeyBytes)
	if err != nil {
		t.Fatal(err)
	}

	var msg = "test-data"

	signed, err := h.Sign([]byte(msg), privateKey)
	if err != nil {
		t.Fatal(err)
	}

	if len(signed) != 256 {
		t.Errorf("SignLength got %d, want %d", len(signed), 256)
	}

	veri, err := h.Verify([]byte(msg), signed, publicKey)
	if err != nil {
		t.Fatal(err)
	}

	if !veri {
		t.Error("Verify fail")
	}

	// ==========

	var check2 = ""
	decoded := fromHex(check2)

	_, err = h.Verify([]byte(msg), decoded, publicKey)
	if err == nil {
		t.Error("Verify true")
	}
}
