func Test_SigningRS256_check(t *testing.T) {
	h := SigningRS256

	var prikey = ""
	var pubkey = ""

	var prikeyBytes = fromBase64(prikey)
	var pubkeyBytes = fromBase64(pubkey)

	privateKey, err := ParseRSAPrivateKeyFromDer(prikeyBytes)
	if err != nil {
		t.Fatal(err)
	}

	publicKey, err := ParseRSAPublicKeyFromDer(pubkeyBytes)
	if err != nil {
		t.Fatal(err)
	}

	var msg = "test-data"

	signed, err := h.Sign([]byte(msg), privateKey)
	if err != nil {
		t.Fatal(err)
	}

	var check = ""

	if len(signed) != 256 {
		t.Errorf("SignLength got %d, want %d", len(signed), 256)
	}

	encoded := fmt.Sprintf("%x", signed)
	if encoded != check {
		t.Errorf("Sign got %s, want %s", encoded, check)
	}

	veri, err := h.Verify([]byte(msg), signed, publicKey)
	if err != nil {
		t.Fatal(err)
	}

	if !veri {
		t.Error("Verify fail")
	}

	// ==========

	var check2 = ""
	decoded := fromHex(check2)

	_, err = h.Verify([]byte(msg), decoded, publicKey)
	if err == nil {
		t.Error("Verify true")
	}
}

func Test_ParseRSAKeyFromDer(t *testing.T) {
	var prikey = ""
	var pubkey = "MF79EwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAE9mdkEmwCjAkiIpa+MyWK7LqwZZWMv2Ft6eNXAKIFAaY11SaJBqLYIVCzewGQv/7yKkChKBDx6dvgfxR0Qm2EKw=="

	var prikeyBytes = fromBase64(prikey)
	var pubkeyBytes = fromBase64(pubkey)

	_, err := ParseRSAPrivateKeyFromDer(prikeyBytes)
	if err == nil {
		t.Error("ParseRSAPrivateKeyFromDer should return error")
	}

	_, err = ParseRSAPublicKeyFromDer(pubkeyBytes)
	if err == nil {
		t.Error("ParseRSAPublicKeyFromDer should return error")
	}
}

